import React, { useRef, useEffect, useState } from "react";
import mapboxgl from "!mapbox-gl"; // eslint-disable-line import/no-webpack-loader-syntax
import axios from "axios";

mapboxgl.accessToken =
  "pk.eyJ1IjoicmVhbC1yYW16aSIsImEiOiJjbGNnOHFtenkwb3hlM25xaHFyYjRsMWo5In0.CVi-C6VaMqej3l0iLgVexw";

export default function App() {
  const mapContainer = useRef(null);
  const map = useRef(null);
  const [lng, setLng] = useState(-70.9);
  const [lat, setLat] = useState(42.35);
  const [zoom, setZoom] = useState(9);
  const [city, setCity] = useState("");

  useEffect(() => {
    if (map.current) return; // initialize map only once
    map.current = new mapboxgl.Map({
      container: mapContainer.current,
      style: "mapbox://styles/mapbox/dark-v11",
      center: [lng, lat],
      zoom: zoom,
    });
  });

  useEffect(() => {
    if (!map.current) return; // wait for map to initialize
    map.current.on("move", () => {
      setLng(map.current.getCenter().lng.toFixed(4));
      setLat(map.current.getCenter().lat.toFixed(4));
      setZoom(map.current.getZoom().toFixed(2));
    });
  });

  const API_KEY = "cf56da8dae59c65a1a46acbba5bafbcd";

  async function getCoordinates(city) {
    try {
      const response = await axios.get(
        `http://api.positionstack.com/v1/forward?access_key=${API_KEY}&query=${city}`
      );
      const data = response.data.data;
      console.log("the raw data ", data);
      if (data[0]) console.log(data[0]);
      else console.log("no data");
      console.log("lat & long");
      console.log(data[0].latitude, data[0].longitude);
      if (data[0]) return [data[0].longitude, data[0].latitude];
      else return null;
    } catch (error) {
      console.error(error);
    }
  }

  async function handleSearch(event) {
    event.preventDefault();
    console.log("---------------------------------------------");
    const coords = await getCoordinates(city);
    console.log(city, coords);
    if (coors == null) {
      setCity("invalid data");
      return;
    }
    setLat(coords[0]);
    setLng(coords[1]);
    console.log(lat, lng);
    map.current.setCenter(coords);
  }

  return (
    <div>
      <div ref={mapContainer} className="map-container">
        <div className="searchbar">
          <form onSubmit={handleSearch}>
            <input
              type="text"
              value={city}
              placeholder="city name"
              onChange={(e) => setCity(e.target.value)}
            ></input>
            <button type="submit">Find city</button>
          </form>
        </div>
        <div className="sidebar">
          Longitude: {lng} | Latitude: {lat} | Zoom: {zoom}
        </div>
      </div>
    </div>
  );
}
