import React, { useState } from "react";
import axios from "axios";
import Searchresult from "./Searchresult";

function Searchbar(props) {
  const API_KEY = "cf56da8dae59c65a1a46acbba5bafbcd";
  const [city, setCity] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  async function getCoordinates(city) {
    try {
      const response = await axios.get(
        `http://api.positionstack.com/v1/forward?access_key=${API_KEY}&query=${city}`
      );
      const data = response.data.data;
      console.log(data);
      if (data.length === 0) return null;
      const reducedSearchResults = data.map((item) => ({
        longitude: item.longitude,
        latitude: item.latitude,
        country: item.country,
        region: item.region,
      }));
      console.log("reducedSearchResults ", reducedSearchResults);
      return reducedSearchResults;
      //   if (data[0]) return [data[0].longitude, data[0].latitude];
      //   else return null;
    } catch (error) {
      console.error(error);
    }
  }

  async function handleSearch(event) {
    event.preventDefault();
    const results = await getCoordinates(city);
    setSearchResults(results);
    if (results) {
      setSearchResults(coords);
    }
    // if (coords === null) {
    //   setCity("invalid data");
    //   return;
    // }

    props.onSearch(coords);
  }

  return (
    <div className="searchbar">
      <form onSubmit={handleSearch}>
        <input
          type="text"
          value={city}
          placeholder="city name"
          onChange={(e) => setCity(e.target.value)}
        ></input>
        <button type="submit">Find city</button>
      </form>

      <ul>{}</ul>
    </div>
  );
}

export default Searchbar;
