import React from "react";
import axios from "axios";

function Searchbar() {
  const API_KEY = "cf56da8dae59c65a1a46acbba5bafbcd";
  const [city, setCity] = useState("");

  async function getCoordinates(city) {
    try {
      const response = await axios.get(
        `http://api.positionstack.com/v1/forward?access_key=${API_KEY}&query=${city}`
      );
      const data = response.data.data;
      // console.log("the raw data ", data);
      // if (data[0]) console.log(data[0]);
      // else console.log("no data");
      // console.log("lat & long");
      // console.log(data[0].latitude, data[0].longitude);
      if (data[0]) return [data[0].longitude, data[0].latitude];
      else return null;
    } catch (error) {
      console.error(error);
    }
  }

  async function handleSearch(event) {
    event.preventDefault();
    // console.log("---------------------------------------------");
    const coords = await getCoordinates(city);
    // console.log(city, coords);
    if (coords == null) {
      setCity("invalid data");
      return;
    }
    setLat(coords[0]);
    setLng(coords[1]);
    console.log(lat, lng);
    map.current.setCenter(coords);
  }

  return (
    <div className="searchbar">
      <form onSubmit={handleSearch}>
        <input
          type="text"
          value={city}
          placeholder="city name"
          onChange={(e) => setCity(e.target.value)}
        ></input>
        <button type="submit">Find city</button>
      </form>
    </div>
  );
}

export default Searchbar;
